package neebalsgurukul.day02;

public class Assignment05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=5;
		for(int i=1;i<=n;i++) {
			for(int j=i;j>0;j--) {
				System.out.print(n-j+1);
				
			}
			System.out.println(" ");
		}
	}

}
